import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

userEmail:string='ds'
      submit(myForm: any){
        console.log(myForm);
    }

  // myForm : FormGroup;
  //   constructor(){
  //       this.myForm = new FormGroup({

  //           "userEmail": new FormControl("", [
  //                               Validators.required,
  //                               Validators.email
  //                           ]),
  //           "userFile": new FormControl("",[Validators.required])
  //       });
  //   }

  //   submit(){
  //       console.log(this.myForm.value.userFile);
  //   }
}
<div class="container w-25">
<form #myForm="ngForm" novalidate (ngSubmit)="submit(myForm)">
    <div class="form-group mt-4">
        <label>Email</label>
        <input class="form-control" name="email" [(ngModel)]]="userEmail" />

        <div class="alert alert-danger"
            *ngIf="myForm.controls['userEmail'].invalid && myForm.controls['userEmail'].touched">
            Некорректный email
        </div>
    </div>
    <div class="form-group mt-4">
       <input type="file" class="file-upload" formControlName="userFile">
    </div>
    <div class="form-group">
        <button class="btn btn-default mt-4" [disabled]="myForm.invalid">
            Send
        </button>
    </div>
  </form>
</div>
