import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { FileService } from 'src/Services/file.service';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  myForm: FormGroup;
  fileType: string = environment.fileType == null ? '' : environment.fileType;
  file?: File;
  status: string = "";

    constructor(private fileService: FileService){
        this.myForm = new FormGroup({
          "userEmail": new FormControl("", [
                                Validators.required,
                                Validators.email
                            ]),
          "userFile": new FormControl(null, [
                                Validators.required,
                                this.requiredFileType])
        });
    }


  onChange(event:any) {
    const file: File = event.target.files[0];
    if (file) {
      this.file = file;
    }
  }

  send(){
    if (this.file && this.myForm.value.userEmail) {
      const formData = new FormData();
      formData.append('email', this.myForm.value.userEmail);
      formData.append('file', this.file, this.file.name);

      this.fileService.upload(formData).subscribe({
        next: (status) => {
          console.log(`Status: ${status}. File is successfully uploaded!`);
          this.status = "File is successfully uploaded!"
        },
        error: err => {
          console.log(err);
          this.status = "File isn't uploaded!"
        }
      });
      this.myForm = new FormGroup({
          "userEmail": new FormControl(),
          "userFile": new FormControl()
      });
      setTimeout(() => {
        this.status = ""
      }, 3000);

    }
  }

  requiredFileType (control: FormControl): {[s:string]:boolean}|null {
    const file = control.value;
    let pattern = "[^\s]+(.*?).(" + environment.fileType + ")$";
    const regex = new RegExp(pattern);
    if ( file ) {
      if ( !regex.test(file)) {
        return {"userFile": true};
      }
    };
    return null;

  }
}

<div class="container w-25">
  <form [formGroup]="myForm" novalidate (ngSubmit)="send()">
    <div class="form-group mt-4">
      <label>Email</label>
      <input class="form-control" name="email" formControlName="userEmail" />

      <div
        class="alert alert-danger"
        *ngIf="
          myForm.controls['userEmail'].invalid &&
          myForm.controls['userEmail'].touched
        "
      >
        Please use a valid email address!
      </div>
    </div>
    <div class="form-group mt-4">
      <input
        type="file"
        class="file-upload"
        formControlName="userFile"
        (change)="onChange($event)"
      />
    </div>
    <div
      class="alert alert-danger"
      *ngIf="
        myForm.controls['userFile'].invalid &&
        myForm.controls['userFile'].touched
      "
    >
      The file format is invalid! Upload file "{{ fileType }} format."
    </div>
    <p class="m">
      File is successfully uploaded!
    </p>
    <div class="form-group">
      <button class="btn btn-info mt-4" [disabled]="myForm.invalid">
        Send
      </button>
    </div>
  </form>
</div>
