import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  myForm : FormGroup;
    constructor(){
        this.myForm = new FormGroup({

            "userEmail": new FormControl("", [
                                Validators.required,
                                Validators.email
                            ]),
            "userFile": new FormControl(null,[Validators.required, this.requiredFileType])
        });
    }

    submit(){
        console.log(this.myForm.value.userFile);
    }

  requiredFileType (control: FormControl): {[s:string]:boolean}|null {
    const file = control.value;
    const regex = new RegExp(/[^\s]+(.*?).(docx)$/);
    if ( file ) {
      const extension = file.name.split('.')[1].toLowerCase();
      if ( extension.toLowerCase() !== "docx" ) {
        return {"userFile": true};
      }
    };
     return null;
  }
}

<div class="container w-25">
<form [formGroup]="myForm" novalidate (ngSubmit)="submit()">
    <div class="form-group mt-4">
        <label>Email</label>
        <input class="form-control" name="email" formControlName="userEmail" />

        <div class="alert alert-danger"
            *ngIf="myForm.controls['userEmail'].invalid && myForm.controls['userEmail'].touched">
            Некорректный email
        </div>
    </div>
    <div class="form-group mt-4">
       <input type="file" class="file-upload" formControlName="userFile">
    </div>
    <div class="form-group">
        <button class="btn btn-default mt-4" [disabled]="myForm.invalid">
            Send
        </button>
    </div>
  </form>
</div>
